package com.softgroup.task1;

import org.junit.Test;
import org.junit.runner.RunWith;
import org.junit.runners.Parameterized;

import java.util.Arrays;
import java.util.Collection;

import static com.softgroup.task1.Main.isPalindrom_ForLoop;
import static com.softgroup.task1.Main.isPalindrom_Recursion;
import static com.softgroup.task1.Main.isPalindrom_Reverse;
import static org.junit.Assert.*;

/**
 * Created by Andrew on 14.04.2017.
 */
@RunWith(Parameterized.class)
public class MainTest {

    @Parameterized.Parameter
    public boolean expected;

    @Parameterized.Parameter(1)
    public String string;

    @Parameterized.Parameters
    public static Collection<Object[]> data() {
        return Arrays.asList(new Object[][] {
                { true, "" +
                        "" },
                { true, "3123312332133213" },
                { true, "1221" },
                { false, "1222" }
        });
    }

    @Test()
    public void forLoopTest() throws Exception {
        assertEquals(expected, isPalindrom_ForLoop(string));
    }

    @Test
    public void recursionTest() throws Exception {
        assertEquals(expected, isPalindrom_Recursion(string));
    }

    @Test
    public void reverseTest() throws Exception {
        assertEquals(expected, isPalindrom_Reverse(string));
    }
}